mapper.select:
SELECT r.ID, r.USER_ID as userId, r.LAYOUT, r.STYLE, r.COL_NUM as colNum, r.CREATE_TIME as createTime, r.UPDATE_TIME as updateTime FROM ODS_LAYOUT_CONFIG r
AND r.USER_ID = #{userId}
ORDER BY CREATE_TIME DESC

SELECT r.ID, r.USER_ID as userId, r.LAYOUT, r.STYLE, r.COL_NUM as colNum, r.CREATE_TIME as createTime, r.UPDATE_TIME as updateTime,r.STATUS,r.REMARK FROM ODS_LAYOUT_CONFIG r
AND r.ID = #{userId} AND r.STATUS = #{status} AND r.USER_ID = #{userId}
ORDER BY CREATE_TIME DESC
